        https://docs.google.com/spreadsheets/d/1hOc7GccePGflrWUQBTZlYz2ATbHhGz7TzSzKvtShIAk
*/

var CONST = {

    // CONST.ID = 9;
    SRC_TYPE:       2,
    SRC_MORN:       4,
    SRC_NOON:       5,
    SRC_EVEN:       6,
    SRC_AFFECT_1:   3, // PERI 1 / ALSH_MERC / NOEL / CARN ...
    SRC_AFFECT_2:   7, // PERI / ALSH PTE VAC / ALSH GDE VAC
