    SRC_FULLNAME:   0,


    // DST REFERENCES
    DST_FULLNAME:   0,
    DST_IS_DIR:     5,
    DST_SCHOOL:     6,

    DST_VOID_1:     1,
    DST_VOID_2:     2,

