                // employee[ CONST.DST_SCHOOL ] = schools[row[ CONST.SRC_AFFECT_3]]; // AFFECT_3 = SCHOOL
            }


            // LAST TIME CONDITION
            // VACATIONS ARE NOT READY YET
            // ONLY FILTER ALSH MERC
