                    'MERC P1':      CONST.DST_ALSH_P1,
                    'NOEL':         CONST.DST_ALSH_NOEL,
                    'CARN':         CONST.DST_ALSH_CARN,
                    'PAQUES':       CONST.DST_ALSH_PAQU,
                    'JUILLET':      CONST.DST_ALSH_JUIL,
                    'AOUT':         CONST.DST_ALSH_AOUT,
